import React, { Component } from 'react';
import {withRouter} from 'react-router';

var empPath = this.props.match.params.id;

class JobComponent extends Component {
  constructor(props) {
      super(props)
      this.state = {
          id: this.props.match.params.employer,
          description: ''
      }
  }
  componentDidMount() {
      console.log(this.state.id)
      // eslint-disable-next-line
      if (this.state.id == -1) {
          return
      }
      JobDataService.retrieveJob(INSTRUCTOR, this.state.employer)
          .then(response => this.setState({
              description: response.data.description
          }))
  }
  render() {
      let { description, id } = this.state
      return (
          <div>
              <h3>Course</h3>
              <div>{id}</div>
              <div>{description}</div>
          </div>
      )
  }
}
export default JobComponent
