import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { Formik, Form, Field, ErrorMessage } from 'formik';

class HomeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
          searchQuery: 'Facebook...'
        }
    }

    render() {
        let searchQuery = this.state; 
        return (
           <>
                <h1>Welcome</h1>
                <div className="container">
                    Welcome {this.props.match.params.username}. You can view the latest jobs <Link to="/jobs">here.</Link>
                    <br></br>
                    <h4>Search a job</h4>
                    <p>You can search for jobs from a certain employer.</p>
                    <div className="container">
                        <Formik
                            initialValues={{searchQuery: searchQuery}}
                        >
                        {
                            (props) => (
                                <Form>
                                <fieldset className="form-group">
                                    <label>Search jobs from an employer</label>
                                    <Field className="form-control" type="text" name="searchQuery" />
                                </fieldset>
                                </Form>
                            )
                        }
                        </Formik>
                    </div>


                    
                </div>
            </>
        )
    }
}

export default HomeComponent
