import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import ReactSearchBox from 'react-search-box'
import JobDataService from '../service/JobDataService';

class HomeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = { 
            jobs: [],
            message: null,
        }
        this.searchJobs = this.searchJobs.bind(this)
    }

    componentDidMount() {   //  React makes componentDidMount be called as soon as the component is mounted
        this.searchJobs(); //  Call searchJobs
    }

    searchJobs(searchQuery) {
        JobDataService.retrieveJobByEmployer(searchQuery)    //  Make call to the REST API
            .then(  //  Decide what to do once call is made succesfully
                response => {
                    //console.log(response);
                    this.setState({ jobs: response.data})  //  When response comes back with data, update the state.
                    console.log(this.state.jobs);
                }
            )   //  .catch handles unsuccessful. Add later
    }

    employers = [
        {
          key: 'John',
          value: 'John Doe',
        },
        {
          key: 'jane',
          value: 'Jane Doe',
        },
        {
          key: 'mary',
          value: 'Mary Phillips',
        },
        {
          key: 'robert',
          value: 'Robert',
        },
        {
          key: 'karius',
          value: 'Karius',
        },
      ]

    render() {
        let data = this.state.jobs
        return (
           <>
                <h1>Welcome</h1>
                <div className="container">
                    Welcome {this.props.match.params.username}. You can view the latest jobs <Link to="/jobs">here.</Link>
                    <br></br>
                    <h4>Search a job</h4>
                    <p>You can search for jobs from a certain employer.</p>
                    <div className="container">
                    <ReactSearchBox
                        placeholder="Placeholder"
                        value="Doe"
                        data={this.state.jobs.value}
                        callback={record => console.log(record)}
                    />
                    </div>


                    
                </div>
            </>
        )
    }
}

export default HomeComponent
