import React, { Component } from 'react'
import AuthenticationService from '../service/AuthenticationService';
import Modal from 'react-awesome-modal';

class LoginComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            username: 'Neil',
            password: '',
            hasLoginFailed: false,
            SuccessfulLogin: false,
            visible : false
        }
        this.handleChange = this.handleChange.bind(this);
        this.loginClicked = this.loginClicked.bind(this);

    }
    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }
    render(){
        return (
            <div>
                <h1>Login</h1>
                <div className="container">
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Failed Login</div>}
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}></input>
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange}></input>
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
                <section>
                <div class="jumbotron jumbotron-fluid">
                <div class="container">
                    <h1 class="display-5">Welcome</h1>
                    <hr class="my-4"></hr>
                    <p class="lead">Welcome {this.props.match.params.username}. You can view the latest jobs <Link to="/jobs">here.</Link></p>
                </div>
                </div>
                <input type="button" value="Open" onClick={() => this.openModal()} />
                <Modal 
                    visible={this.state.visible}
                    width="400"
                    height="300"
                    effect="fadeInUp"
                    onClickAway={() => this.closeModal()}
                >
                    <div>
                        <h1>Title</h1>
                        <p>Some Contents</p>
                        <a href="javascript:void(0);" onClick={() => this.closeModal()}>Close</a>
                    </div>
                </Modal>
            </section>
            </div>
        )
    }
    //  This is a generic change handler rather than having to have a method for each element!
    handleChange(event){
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        );
    }

    loginClicked(){
        // if(this.state.username === "Neil" && this.state.password === "password"){
        //     //  Send username and password to authentication service
        //     AuthenticationService.registerLogin(this.state.username, this.state.password)
        //     this.setState(
        //         {
        //             SuccessfulLogin: true,
        //             hasLoginFailed: false,
        //         }
        //     )
        //     this.props.history.push(`/home/${this.state.username}`)
        // } else{
        //     this.setState(
        //         {
        //             hasLoginFailed: true,
        //             SuccessfulLogin: false
        //         }
        //     )
        // }
        // AuthenticationService.executeBasicAuthenticationService(this.state.username, this.state.password)
        // .then(
        //     () => {
        //         AuthenticationService.registerLogin(this.state.username, this.state.password)
        //         this.setState({ SuccessfulLogin: true, hasLoginFailed: false})  //  dont need!!!!!!!!!!!!!!!!!!
        //         this.props.history.push(`/home/${this.state.username}`)
        //     }
        // ).catch( ()=> {
        //     this.setState({hasLoginFailed: true})
        //     this.setState({SuccessfulLogin: false})
        // })

        AuthenticationService
            .executeJwtAuthenticationService(this.state.username, this.state.password)
            .then((response) => {
                AuthenticationService.registerSuccessfulLoginForJwt(this.state.username, response.data.token)
                this.props.history.push(`/home/${this.state.username}`)
            }).catch(() => {
                this.setState({ showSuccessMessage: false })
                this.setState({ hasLoginFailed: true })
            })
    }
}

export default LoginComponent
