import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import JobDataService from '../service/JobDataService';

class HomeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = { 
            searchQuery: 'Facebook',
        }
        this.handleChange = this.handleChange.bind(this);
    }

    render() {
        return (
           <>
                <h1>Welcome</h1>
                <div className="container">
                    Welcome {this.props.match.params.username}. You can view the latest jobs <Link to="/jobs">here.</Link>
                    <br></br>
                    <h4>Search a job</h4>
                    <p>You can search for jobs from a certain employer.</p>
                    <div className="container">
                    <input type="text" name="searchQuery" value={this.state.username} onChange={this.handleChange}></input>
                    <button className="btn btn-success" onClick={this.searchClicked}>Login</button>
                    </div>   
                </div>
            </>
        )
    }

    searchClicked(){
        JobDataService
            .retrieveJobByEmployer(this.state.searchQuery)
            .then((response) => {
                AuthenticationService.registerSuccessfulLoginForJwt(this.state.username, response.data.token)
                this.props.history.push(`/home/${this.state.username}`)
            })
    }

    handleChange(event){
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        );
    }
}

export default HomeComponent
